mapper.update:
UPDATE DOCAPPROVE
SET
APPROVE_STATUS = #{approveStatus}
WHERE
ADOC_NO = #{adocNo}
AND
EMP_NO = #{empNo}

UPDATE SIGNFILE
SET
SIGN_ORIGIN = #{signOrigin},
SIGN_EDIT = #{signEdit} 
WHERE
EMP_NO = #{empNo}

UPDATE APPROVEDOCUMENT
SET
ADOC_NAME = #{adocName},
ADOC_CONTENT = #{adocContent}
WHERE
EMP_NO = #{empNo}
AND
ADOC_NO = #{adocNo}
